## 3. JSON Schema for Input Validation

### 3.1 Primary Audit Request Schema

```json
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://sre.internal/schemas/a-cfo-solvency-audit/v2.1.0",
  "title": "A-CFO-SolvencyAudit-Request",
  "type": "object",
  "required": ["auditId", "timestamp", "entityScope", "assetPortfolio", "liabilityPortfolio", "parameters"],
  "properties": {
    "auditId": {
      "type": "string",
      "pattern": "^AUD-[0-9]{4}-[A-Z]{2}-[0-9]{8}$",
      "description": "Unique audit identifier"
    },
    "timestamp": {
      "type": "string",
      "format": "date-time",
      "description": "ISO 8601 UTC timestamp"
    },
    "entityScope": {
      "type": "object",
      "required": ["primaryEntity", "consolidationType"],
      "properties": {
        "primaryEntity": {
          "type": "string",
          "pattern": "^ENT-[0-9]{6}$"
        },
        "subsidiaries": {
          "type": "array",
          "items": {
            "type": "string",
            "pattern": "^ENT-[0-9]{6}$"
          },
          "maxItems": 500
        },
        "consolidationType": {
          "type": "string",
          "enum": ["FULL", "PROPORTIONAL", "EQUITY_METHOD", "STANDALONE"]
        },
        "eliminationRules": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "ruleId": { "type": "string" },
              "sourceEntity": { "type": "string" },
              "targetEntity": { "type": "string" },
              "eliminationType": { 
                "type": "string",
                "enum": ["INTERCOMPANY_RECEIVABLE", "INTERCOMPANY_PAYABLE", "INVESTMENT", "DIVIDEND"]
              }
            }
          }
        }
      }
    },
    "assetPortfolio": {
      "type": "object",
      "required": ["currentAssets", "nonCurrentAssets", "valuationDate"],
      "properties": {
        "valuationDate": {
          "type": "string",
          "format": "date"
        },
        "baseCurrency": {
          "type": "string",
          "pattern": "^[A-Z]{3}$",
          "default": "USD"
        },
        "currentAssets": {
          "type": "object",
          "properties": {
            "cashAndEquivalents": {
              "$ref": "#/$defs/monetaryAmount"
            },
            "marketableSecurities": {
              "$ref": "#/$defs/monetaryAmount"
            },
            "accountsReceivable": {
              "type": "object",
              "properties": {
                "gross": { "$ref": "#/$defs/monetaryAmount" },
                "allowanceForDoubtful": { "$ref": "#/$defs/monetaryAmount" },
                "agingBuckets": {
                  "type": "object",
                  "properties": {
                    "current": { "$ref": "#/$defs/monetaryAmount" },
                    "days30": { "$ref": "#/$defs/monetaryAmount" },
                    "days60": { "$ref": "#/$defs/monetaryAmount" },
                    "days90": { "$ref": "#/$defs/monetaryAmount" },
                    "days90Plus": { "$ref": "#/$defs/monetaryAmount" }
                  }
                }
              }
            },
            "inventory": {
              "type": "object",
              "properties": {
                "rawMaterials": { "$ref": "#/$defs/monetaryAmount" },
                "workInProgress": { "$ref": "#/$defs/monetaryAmount" },
                "finishedGoods": { "$ref": "#/$defs/monetaryAmount" },
                "valuationMethod": {
                  "type": "string",
                  "enum": ["FIFO", "LIFO", "WEIGHTED_AVERAGE", "SPECIFIC_ID"]
                }
              }
            },
            "prepaidExpenses": { "$ref": "#/$defs/monetaryAmount" }
          }
        },
        "nonCurrentAssets": {
          "type": "object",
          "properties": {
            "propertyPlantEquipment": {
              "type": "object",
              "properties": {
                "grossValue": { "$ref": "#/$defs/monetaryAmount" },
                "accumulatedDepreciation": { "$ref": "#/$defs/monetaryAmount" },
                "impairmentProvision": { "$ref": "#/$defs/monetaryAmount" }
              }
            },
            "intangibleAssets": {
              "type": "object",
              "properties": {
                "goodwill": { "$ref": "#/$defs/monetaryAmount" },
                "patents": { "$ref": "#/$defs/monetaryAmount" },
                "trademarks": { "$ref": "#/$defs/monetaryAmount" },
                "software": { "$ref": "#/$defs/monetaryAmount" },
                "amortization": { "$ref": "#/$defs/monetaryAmount" }
              }
            },
            "longTermInvestments": { "$ref": "#/$defs/monetaryAmount" },
            "deferredTaxAssets": { "$ref": "#/$defs/monetaryAmount" }
          }
        }
      }
    },
    "liabilityPortfolio": {
      "type": "object",
      "required": ["currentLiabilities", "nonCurrentLiabilities"],
      "properties": {
        "currentLiabilities": {
          "type": "object",
          "properties": {
            "accountsPayable": { "$ref": "#/$defs/monetaryAmount" },
            "shortTermDebt": {
              "type": "array",
              "items": {
                "$ref": "#/$defs/debtInstrument"
              }
            },
            "currentPortionLongTermDebt": { "$ref": "#/$defs/monetaryAmount" },
            "accruedExpenses": { "$ref": "#/$defs/monetaryAmount" },
            "deferredRevenue": { "$ref": "#/$defs/monetaryAmount" },
            "taxesPayable": { "$ref": "#/$defs/monetaryAmount" }
          }
        },
        "nonCurrentLiabilities": {
          "type": "object",
          "properties": {
            "longTermDebt": {
              "type": "array",
              "items": {
                "$ref": "#/$defs/debtInstrument"
              }
            },
            "pensionObligations": { "$ref": "#/$defs/monetaryAmount" },
            "deferredTaxLiabilities": { "$ref": "#/$defs/monetaryAmount" },
            "leaseObligations": { "$ref": "#/$defs/monetaryAmount" },
            "contingentLiabilities": {
              "type": "array",
              "items": {
                "type": "object",
                "properties": {
                  "description": { "type": "string" },
                  "estimatedAmount": { "$ref": "#/$defs/monetaryAmount" },
                  "probability": { 
                    "type": "number",
                    "minimum": 0,
                    "maximum": 1
                  },
                  "classification": {
                    "type": "string",
                    "enum": ["PROBABLE", "POSSIBLE", "REMOTE"]
                  }
                }
              }
            }
          }
        }
      }
    },
    "parameters": {
      "type": "object",
      "properties": {
        "stressTestScenarios": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["BASE", "MILD_RECESSION", "SEVERE_RECESSION", "LIQUIDITY_CRISIS", "MARKET_SHOCK"]
          },
          "default": ["BASE"]
        },
        "confidenceLevel": {
          "type": "number",
          "minimum": 0.90,
          "maximum": 0.99,
          "default": 0.95
        },
        "timeHorizon": {
          "type": "object",
          "properties": {
            "value": { "type": "integer", "minimum": 1, "maximum": 120 },
            "unit": { "type": "string", "enum": ["DAYS", "MONTHS", "QUARTERS", "YEARS"] }
          }
        },
        "regulatoryFramework": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["GAAP", "IFRS", "BASEL_III", "SOLVENCY_II", "SOX"]
          }
        }
      }
    }
  },
  "$defs": {
    "monetaryAmount": {
      "type": "object",
      "required": ["value", "currency"],
      "properties": {
        "value": {
          "type": "number",
          "minimum": -999999999999.99,
          "maximum": 999999999999.99
        },
        "currency": {
          "type": "string",
          "pattern": "^[A-Z]{3}$"
        },
        "precision": {
          "type": "integer",
          "minimum": 0,
          "maximum": 8,
          "default": 2
        }
      }
    },
    "debtInstrument": {
      "type": "object",
      "required": ["instrumentId", "principal", "interestRate", "maturityDate"],
      "properties": {
        "instrumentId": {
          "type": "string",
          "pattern": "^DEBT-[A-Z0-9]{8}$"
        },
        "instrumentType": {
          "type": "string",
          "enum": ["TERM_LOAN", "REVOLVER", "BOND", "CONVERTIBLE", "COMMERCIAL_PAPER", "LEASE"]
        },
        "principal": { "$ref": "#/$defs/monetaryAmount" },
        "interestRate": {
          "type": "object",
          "properties": {
            "type": { "type": "string", "enum": ["FIXED", "FLOATING"] },
            "baseRate": { "type": "number", "minimum": 0, "maximum": 1 },
