## 4. JSON Schema Specifications

### 4.1 Brand Configuration Input Schema

```json
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://a-cmo.io/schemas/brand-tuner/config/v2",
  "title": "BrandTunerConfiguration",
  "type": "object",
  "required": ["brand_id", "tenant_id", "config_version", "brand_profile", "tuning_parameters", "alert_thresholds"],
  "additionalProperties": false,
  "properties": {
    "brand_id": {
      "type": "string",
      "pattern": "^BRD-[A-Z0-9]{8}$",
      "description": "Unique brand identifier"
    },
    "tenant_id": {
      "type": "string",
      "pattern": "^TNT-[A-Z0-9]{6}$"
    },
    "config_version": {
      "type": "string",
      "pattern": "^\\d+\\.\\d+\\.\\d+$"
    },
    "brand_profile": {
      "type": "object",
      "required": ["name", "industry", "market_segment", "competitors"],
      "properties": {
        "name": {
          "type": "string",
          "minLength": 1,
          "maxLength": 256
        },
        "industry": {
          "type": "string",
          "enum": ["technology", "finance", "healthcare", "retail", "manufacturing", "media", "automotive", "energy", "telecom", "other"]
        },
        "market_segment": {
          "type": "string",
          "enum": ["enterprise", "smb", "consumer", "mixed"]
        },
        "competitors": {
          "type": "array",
          "minItems": 1,
          "maxItems": 20,
          "items": {
            "type": "object",
            "required": ["competitor_id", "name", "weight"],
            "properties": {
              "competitor_id": {
                "type": "string",
                "pattern": "^CMP-[A-Z0-9]{8}$"
              },
              "name": {
                "type": "string"
              },
              "weight": {
                "type": "number",
                "minimum": 0.1,
                "maximum": 1.0
              }
            }
          }
        },
        "brand_keywords": {
          "type": "array",
          "maxItems": 100,
          "items": {
            "type": "string",
            "minLength": 2,
            "maxLength": 64
          }
        }
      }
    },
    "tuning_parameters": {
      "type": "object",
      "required": ["weights", "channels", "budget_constraints"],
      "properties": {
        "weights": {
          "type": "object",
          "required": ["awareness", "sentiment", "engagement", "loyalty"],
          "properties": {
            "awareness": { "type": "number", "minimum": 0, "maximum": 1 },
            "sentiment": { "type": "number", "minimum": 0, "maximum": 1 },
            "engagement": { "type": "number", "minimum": 0, "maximum": 1 },
            "loyalty": { "type": "number", "minimum": 0, "maximum": 1 }
          },
          "additionalProperties": false
        },
        "channels": {
          "type": "array",
          "minItems": 1,
          "items": {
            "type": "object",
            "required": ["channel_id", "type", "enabled", "elasticity_override"],
            "properties": {
              "channel_id": {
                "type": "string",
                "pattern": "^CHN-[A-Z0-9]{6}$"
              },
              "type": {
                "type": "string",
                "enum": ["social", "search", "display", "email", "content", "pr", "influencer", "podcast", "video"]
              },
              "enabled": { "type": "boolean" },
              "elasticity_override": {
                "type": ["number", "null"],
                "minimum": 0.01,
                "maximum": 0.50
              }
            }
          }
        },
        "budget_constraints": {
          "type": "object",
          "required": ["total_monthly", "currency", "allocation_mode"],
          "properties": {
            "total_monthly": {
              "type": "number",
              "minimum": 0,
              "maximum": 100000000
            },
            "currency": {
              "type": "string",
              "pattern": "^[A-Z]{3}$"
            },
            "allocation_mode": {
              "type": "string",
              "enum": ["fixed", "dynamic", "hybrid"]
            },
            "channel_caps": {
              "type": "object",
              "additionalProperties": {
                "type": "number",
                "minimum": 0,
                "maximum": 1
              }
            }
          }
        },
        "tuning_frequency": {
          "type": "string",
          "enum": ["realtime", "hourly", "daily", "weekly"],
          "default": "daily"
        },
        "smoothing_factor": {
          "type": "number",
          "minimum": 0.1,
          "maximum": 0.9,
          "default": 0.3
        }
      }
    },
    "alert_thresholds": {
      "type": "object",
      "required": ["bhs_critical", "bhs_warning", "bvi_negative_threshold", "cps_floor"],
      "properties": {
        "bhs_critical": {
          "type": "number",
          "minimum": 0,
          "maximum": 0.5
        },
        "bhs_warning": {
          "type": "number",
          "minimum": 0.2,
          "maximum": 0.7
        },
        "bvi_negative_threshold": {
          "type": "number",
          "minimum": -0.2,
          "maximum": 0
        },
        "cps_floor": {
          "type": "number",
          "minimum": -3,
          "maximum": 0
        },
        "sentiment_spike_threshold": {
          "type": "number",
          "minimum": 0.1,
          "maximum": 0.5,
          "default": 0.25
        }
      }
    },
    "data_sources": {
      "type": "array",
      "items": {
        "type": "object",
        "required": ["source_id", "type", "priority", "refresh_interval_seconds"],
        "properties": {
          "source_id": {
            "type": "string",
            "pattern": "^SRC-[A-Z0-9]{8}$"
          },
          "type": {
            "type": "string",
            "enum": ["social_api", "web_scraper", "survey", "crm", "analytics", "custom"]
          },
          "priority": {
            "type": "integer",
            "minimum": 1,
            "maximum": 10
          },
          "refresh_interval_seconds": {
            "type": "integer",
            "minimum": 60,
            "maximum": 86400
          },
          "credentials_ref": {
            "type": "string",
            "pattern": "^vault://[a-z0-9-]+/[a-z0-9-]+$"
          }
        }
      }
    },
    "metadata": {
      "type": "object",
      "properties": {
        "created_at": { "type": "string", "format": "date-time" },
        "updated_at":
```}}}}]]]
